use std::sync::Arc;

use anyhow::Result;
use tabled::{settings::Style, Table, Tabled};
use venues::binance::coinm::{AccountRequest, PrivateRestClient};

#[derive(Tabled)]
pub struct AssetRow {
    #[tabled(rename = "Asset")]
    pub asset: String,

    #[tabled(rename = "Wallet Balance")]
    pub wallet_balance: String,

    #[tabled(rename = "Unrealized PNL")]
    pub unrealized_profit: String,

    #[tabled(rename = "Margin Balance")]
    pub margin_balance: String,
}

pub async fn handle_account_command(client: Arc<PrivateRestClient>) -> Result<()> {
    let request = AccountRequest {
        timestamp: chrono::Utc::now().timestamp_millis() as u64,
        recv_window: None,
    };
    let account = client.get_account(request).await?;

    if account.data.assets.is_empty() {
        println!("No assets found in account");
        return Ok(());
    }

    let mut rows: Vec<AssetRow> = account
        .data
        .assets
        .iter()
        .map(|asset| AssetRow {
            asset: asset.asset.clone(),
            wallet_balance: asset.wallet_balance.to_string(),
            unrealized_profit: asset.unrealized_profit.to_string(),
            margin_balance: asset.margin_balance.to_string(),
        })
        .collect();
    rows.sort_by(|a, b| a.asset.cmp(&b.asset));

    let mut table = Table::new(rows);
    table.with(Style::rounded());
    println!("{table}");

    Ok(())
}

use venues::binance::coinm::{AllOrdersRequest, PrivateRestClient};

pub async fn run_all_orders(client: &PrivateRestClient, symbol: String, limit: u32) {
    let params = AllOrdersRequest {
        symbol: Some(symbol),
        pair: None,
        order_id: None,
        start_time: None,
        end_time: None,
        limit: Some(limit),
        recv_window: None,
        timestamp: chrono::Utc::now().timestamp_millis() as u64,
    };
    match client.get_all_orders(params).await {
        Ok(orders) => {
            println!("All Orders:");
            for order in orders.data {
                println!("{order:#?}");
            }
        }
        Err(e) => eprintln!("Error fetching all orders: {e:?}"),
    }
}

use std::sync::Arc;

use anyhow::{anyhow, Result};
use venues::binance::coinm::{
    BatchOrderRequest, BatchOrderResult, OrderSide, OrderType, PlaceBatchOrdersRequest,
    PrivateRestClient, TimeInForce,
};

pub async fn handle_batch_order_command(
    client: Arc<PrivateRestClient>,
    symbol: String,
    side: String,
    order_type: String,
    quantity: f64,
    price: Option<f64>,
) -> Result<()> {
    let side = match side.to_uppercase().as_str() {
        "BUY" => OrderSide::Buy,
        "SELL" => OrderSide::Sell,
        _ => return Err(anyhow!("Invalid side. Must be 'BUY' or 'SELL'")),
    };

    let order_type = match order_type.to_uppercase().as_str() {
        "LIMIT" => OrderType::Limit,
        "MARKET" => OrderType::Market,
        _ => return Err(anyhow!("Invalid order type. Must be 'LIMIT' or 'MARKET'")),
    };

    let now = chrono::Utc::now().timestamp_millis() as u64;

    // Create a batch order request
    let batch_req = BatchOrderRequest {
        symbol: symbol.clone(),
        side,
        position_side: None,
        order_type,
        time_in_force: Some(TimeInForce::GTC),
        quantity: quantity.to_string(),
        reduce_only: None,
        price: price.map(|p| p.to_string()),
        new_client_order_id: None,
        stop_price: None,
        activation_price: None,
        callback_rate: None,
        working_type: None,
        price_protect: None,
        new_order_resp_type: None,
        price_match: None,
        self_trade_prevention_mode: None,
    };

    let batch_req2 = BatchOrderRequest {
        symbol: symbol.clone(),
        side,
        position_side: None,
        order_type,
        time_in_force: Some(TimeInForce::GTC),
        quantity: "200".to_string(),
        reduce_only: None,
        price: price.map(|p| p.to_string()),
        new_client_order_id: None,
        stop_price: None,
        activation_price: None,
        callback_rate: None,
        working_type: None,
        price_protect: None,
        new_order_resp_type: None,
        price_match: None,
        self_trade_prevention_mode: None,
    };

    let request = PlaceBatchOrdersRequest {
        batch_orders: vec![batch_req, batch_req2],
        recv_window: None,
        timestamp: now,
    };

    let response = client.place_batch_orders(request).await?;

    // Print the results
    println!("Batch order placed for {symbol}:");
    for (i, order) in response.data.iter().enumerate() {
        match order {
            BatchOrderResult::Ok(order) => {
                println!("\nOrder {}:", i + 1);
                println!("  Order ID: {}", order.order_id);
                println!("  Client Order ID: {}", order.client_order_id);
                println!("  Status: {:?}", order.status);
                println!("  Price: {}", order.price);
                println!("  Quantity: {}", order.orig_qty);
                println!("  Executed Quantity: {}", order.executed_qty);
                println!("  Average Price: {}", order.avg_price);
            }
            BatchOrderResult::Err(err) => {
                println!("\nOrder {}:", i + 1);
                println!("  Error: {err:?}");
            }
        }
    }

    Ok(())
}

use std::sync::Arc;

use anyhow::Result;
use tabled::{settings::Style, Table, Tabled};
use venues::binance::coinm::{ExchangeInfoResponse, Filter, PublicRestClient};

/// Handles the `exchange-info` command: fetches and prints exchange information.
pub async fn handle_exchange_info_command(client: Arc<PublicRestClient>) -> Result<()> {
    let response = client.get_exchange_info().await?;
    print_exchange_info(&response.data);
    Ok(())
}

#[derive(Tabled)]
struct RateLimitRow {
    #[tabled(rename = "Type")]
    rate_limit_type: String,
    #[tabled(rename = "Interval")]
    interval: String,
    #[tabled(rename = "Interval Num")]
    interval_num: u32,
    #[tabled(rename = "Limit")]
    limit: u32,
}

#[derive(Tabled)]
struct SymbolRow {
    #[tabled(rename = "Symbol")]
    symbol: String,
    #[tabled(rename = "Pair")]
    pair: String,
    #[tabled(rename = "Status")]
    status: String,
    #[tabled(rename = "Contract Type")]
    contract_type: String,
}

fn print_exchange_info(info: &ExchangeInfoResponse) {
    println!("Exchange Timezone: {}", info.timezone);
    println!("Symbols:");

    let mut symbol_rows: Vec<SymbolRow> = info
        .symbols
        .iter()
        .map(|symbol| SymbolRow {
            symbol: symbol.symbol.clone(),
            pair: symbol.pair.clone(),
            status: format!("{:?}", symbol.contract_status),
            contract_type: format!("{:?}", symbol.contract_type),
        })
        .collect();

    //sort symbol_rows by contract type and then pair and then symbol
    symbol_rows.sort_by(|a, b| {
        (a.contract_type.as_str(), a.pair.as_str(), a.symbol.as_str()).cmp(&(
            b.contract_type.as_str(),
            b.pair.as_str(),
            b.symbol.as_str(),
        ))
    });

    let mut symbol_table = Table::new(symbol_rows);
    symbol_table.with(Style::rounded());

    println!("{symbol_table}");
    println!("\nRate Limits:");

    let rows: Vec<RateLimitRow> = info
        .rate_limits
        .iter()
        .map(|rl| RateLimitRow {
            rate_limit_type: format!("{:?}", rl.rate_limit_type),
            interval: format!("{:?}", rl.interval),
            interval_num: rl.interval_num,
            limit: rl.limit,
        })
        .collect();

    let mut table = Table::new(rows);
    table.with(Style::rounded());
    println!("{table}");

    println!("\nFilters:");

    // PriceFilter Table
    #[derive(Tabled)]
    struct PriceFilterRow {
        #[tabled(rename = "Symbol")]
        symbol: String,
        #[tabled(rename = "Min Price")]
        min_price: String,
        #[tabled(rename = "Max Price")]
        max_price: String,
        #[tabled(rename = "Tick Size")]
        tick_size: String,
    }
    let mut price_filter_rows = Vec::new();

    // LotSizeFilter Table
    #[derive(Tabled)]
    struct LotSizeFilterRow {
        #[tabled(rename = "Symbol")]
        symbol: String,
        #[tabled(rename = "Min Qty")]
        min_qty: String,
        #[tabled(rename = "Max Qty")]
        max_qty: String,
        #[tabled(rename = "Step Size")]
        step_size: String,
    }
    let mut lot_size_filter_rows = Vec::new();

    // MarketLotSizeFilter Table
    #[derive(Tabled)]
    struct MarketLotSizeFilterRow {
        #[tabled(rename = "Symbol")]
        symbol: String,
        #[tabled(rename = "Min Qty")]
        min_qty: String,
        #[tabled(rename = "Max Qty")]
        max_qty: String,
        #[tabled(rename = "Step Size")]
        step_size: String,
    }
    let mut market_lot_size_filter_rows = Vec::new();

    // MaxNumOrdersFilter Table
    #[derive(Tabled)]
    struct MaxNumOrdersFilterRow {
        #[tabled(rename = "Symbol")]
        symbol: String,
        #[tabled(rename = "Limit")]
        limit: String,
    }
    let mut max_num_orders_filter_rows = Vec::new();

    // PercentPriceFilter Table
    #[derive(Tabled)]
    struct PercentPriceFilterRow {
        #[tabled(rename = "Symbol")]
        symbol: String,
        #[tabled(rename = "Multiplier Down")]
        multiplier_down: String,
        #[tabled(rename = "Multiplier Up")]
        multiplier_up: String,
        #[tabled(rename = "Multiplier Decimal")]
        multiplier_decimal: String,
    }
    let mut percent_price_filter_rows = Vec::new();

    for symbol in &info.symbols {
        for filter in &symbol.filters {
            match filter {
                Filter::PriceFilter(f) => {
                    price_filter_rows.push(PriceFilterRow {
                        symbol: symbol.symbol.clone(),
                        min_price: f.min_price.clone().unwrap_or_default(),
                        max_price: f.max_price.clone().unwrap_or_default(),
                        tick_size: f.tick_size.clone().unwrap_or_default(),
                    });
                }
                Filter::LotSizeFilter(f) => {
                    lot_size_filter_rows.push(LotSizeFilterRow {
                        symbol: symbol.symbol.clone(),
                        min_qty: f.min_qty.clone().unwrap_or_default(),
                        max_qty: f.max_qty.clone().unwrap_or_default(),
                        step_size: f.step_size.clone().unwrap_or_default(),
                    });
                }
                Filter::MarketLotSizeFilter(f) => {
                    market_lot_size_filter_rows.push(MarketLotSizeFilterRow {
                        symbol: symbol.symbol.clone(),
                        min_qty: f.min_qty.clone().unwrap_or_default(),
                        max_qty: f.max_qty.clone().unwrap_or_default(),
                        step_size: f.step_size.clone().unwrap_or_default(),
                    });
                }
                Filter::MaxNumOrdersFilter(f) => {
                    max_num_orders_filter_rows.push(MaxNumOrdersFilterRow {
                        symbol: symbol.symbol.clone(),
                        limit: f.limit.map(|v| v.to_string()).unwrap_or_default(),
                    });
                }
                Filter::PercentPriceFilter(f) => {
                    percent_price_filter_rows.push(PercentPriceFilterRow {
                        symbol: symbol.symbol.clone(),
                        multiplier_down: f.multiplier_down.clone().unwrap_or_default(),
                        multiplier_up: f.multiplier_up.clone().unwrap_or_default(),
                        multiplier_decimal: f
                            .multiplier_decimal
                            .as_ref()
                            .map(|v| v.to_string())
                            .unwrap_or_default(),
                    });
                }
                Filter::MaxNumAlgoOrdersFilter(_) => {
                    // Not displayed in table
                }
                Filter::Unknown => {
                    // Not displayed in table
                }
            }
        }
    }

    if !price_filter_rows.is_empty() {
        println!("\nPRICE_FILTER:");
        let mut table = Table::new(price_filter_rows);
        table.with(Style::rounded());
        println!("{table}");
    }
    if !lot_size_filter_rows.is_empty() {
        println!("\nLOT_SIZE:");
        let mut table = Table::new(lot_size_filter_rows);
        table.with(Style::rounded());
        println!("{table}");
    }
    if !market_lot_size_filter_rows.is_empty() {
        println!("\nMARKET_LOT_SIZE:");
        let mut table = Table::new(market_lot_size_filter_rows);
        table.with(Style::rounded());
        println!("{table}");
    }
    if !max_num_orders_filter_rows.is_empty() {
        println!("\nMAX_NUM_ORDERS:");
        let mut table = Table::new(max_num_orders_filter_rows);
        table.with(Style::rounded());
        println!("{table}");
    }
    if !percent_price_filter_rows.is_empty() {
        println!("\nPERCENT_PRICE:");
        let mut table = Table::new(percent_price_filter_rows);
        table.with(Style::rounded());
        println!("{table}");
    }
}

pub mod account;
pub mod all_orders;
pub mod batch_order;
pub mod exchange_info;
pub mod order;
pub mod position_risk;
pub mod trades;

pub use account::handle_account_command;
pub use batch_order::handle_batch_order_command;
use clap::Subcommand;
pub use exchange_info::handle_exchange_info_command;
pub use order::handle_order_command;
pub use trades::handle_trades_command;

#[derive(Subcommand)]
pub enum Commands {
    /// Get account information including balances and positions
    Account,

    /// Get recent trades for a symbol
    Trades {
        /// Trading symbol (e.g., BTCUSD_PERP)
        #[arg(short, long)]
        symbol: String,

        /// Maximum number of trades to fetch
        #[arg(short, long, default_value = "100")]
        limit: u32,
    },

    /// Place a batch of orders
    BatchOrder {
        /// Trading symbol (e.g., BTCUSD_PERP)
        #[arg(short, long)]
        symbol: String,

        /// Order side (BUY or SELL)
        #[arg(short, long, value_parser = ["BUY", "SELL"])]
        side: String,

        /// Order type (LIMIT or MARKET)
        #[arg(short, long, value_parser = ["LIMIT", "MARKET"])]
        order_type: String,

        /// Order quantity
        #[arg(short, long)]
        quantity: String,

        /// Order price (required for LIMIT orders)
        #[arg(short, long)]
        price: Option<String>,

        /// Client order ID
        #[arg(long)]
        client_order_id: Option<String>,
    },

    /// Get exchange information (trading rules, symbols, rate limits)
    ExchangeInfo,

    /// Place a new order
    Order {
        /// Trading symbol (e.g., BTCUSD_200925)
        #[arg(short, long)]
        symbol: String,
        /// Order side (BUY or SELL)
        #[arg(short, long)]
        side: String,
        /// Order type (LIMIT or MARKET)
        #[arg(short, long)]
        order_type: String,
        /// Order quantity
        #[arg(short, long)]
        quantity: Option<f64>,
        /// Order price (required for LIMIT)
        #[arg(short, long)]
        price: Option<f64>,
    },

    /// Get all orders for a symbol
    AllOrders {
        /// Trading symbol (e.g., BTCUSD_200925)
        #[arg(short, long)]
        symbol: String,
        /// Limit number of orders
        #[arg(short, long, default_value = "10")]
        limit: u32,
    },

    /// Get position risk (all positions)
    PositionRisk,
}

pub async fn handle_all_orders_command(
    client: std::sync::Arc<venues::binance::coinm::PrivateRestClient>,
    symbol: String,
    limit: u32,
) -> anyhow::Result<()> {
    crate::commands::all_orders::run_all_orders(&client, symbol, limit).await;
    Ok(())
}

pub async fn handle_position_risk_command(
    client: std::sync::Arc<venues::binance::coinm::PrivateRestClient>,
) -> anyhow::Result<()> {
    crate::commands::position_risk::run_position_risk(&client).await;
    Ok(())
}

use std::sync::Arc;

use anyhow::{anyhow, Result};
use venues::binance::coinm::{NewOrderRequest, OrderSide, OrderType, PrivateRestClient};

/// Example command to place a new order using the CLI.
pub async fn handle_order_command(
    client: Arc<PrivateRestClient>,
    symbol: String,
    side: String,
    order_type: String,
    quantity: Option<f64>,
    price: Option<f64>,
) -> Result<()> {
    let side = match side.to_uppercase().as_str() {
        "BUY" => OrderSide::Buy,
        "SELL" => OrderSide::Sell,
        _ => return Err(anyhow!("Invalid side. Must be 'BUY' or 'SELL'")),
    };
    let order_type = match order_type.to_uppercase().as_str() {
        "LIMIT" => OrderType::Limit,
        "MARKET" => OrderType::Market,
        _ => return Err(anyhow!("Invalid order type. Must be 'LIMIT' or 'MARKET'")),
    };
    let now = chrono::Utc::now().timestamp_millis() as u64;
    let req = NewOrderRequest {
        symbol,
        side,
        order_type,
        time_in_force: None, // Some(TimeInForce::GTC),
        quantity: quantity.map(|q| q.to_string()),
        price: price.map(|p| p.to_string()),
        timestamp: now,
        new_client_order_id: None,        // Optional, can be set if needed
        position_side: None,              // Optional, can be set if needed
        reduce_only: None,                // Optional, can be set if needed
        stop_price: None,                 // Optional, can be set if needed
        close_position: None,             // Optional, can be set if needed
        activation_price: None,           // Optional, can be set if needed
        callback_rate: None,              // Optional, can be set if needed
        working_type: None,               // Optional, can be set if needed
        price_protect: None,              // Optional, can be set if needed
        recv_window: None,                // Optional, can be set if needed
        new_order_resp_type: None,        // Optional, can be set if needed
        price_match: None,                // Optional, can be set if needed
        self_trade_prevention_mode: None, // Optional, can be set if needed
    };
    let resp = client.post_order(req).await?;
    println!(
        "Order placed: order_id={}, status={}, executed_qty={}",
        resp.data.order_id, resp.data.status, resp.data.executed_qty
    );
    Ok(())
}

use venues::binance::coinm::{PositionRiskRequest, PrivateRestClient};

pub async fn run_position_risk(client: &PrivateRestClient) {
    // Example: fetch all positions
    let params = PositionRiskRequest {
        margin_asset: None,
        pair: None,
        recv_window: None,
        timestamp: chrono::Utc::now().timestamp_millis() as u64,
    };

    match client.get_position_risk(params).await {
        Ok(positions) => {
            println!("Position Risk:");
            for pos in positions.data {
                println!("{pos:#?}");
            }
        }
        Err(e) => eprintln!("Error fetching position risk: {e:?}"),
    }
}

use std::sync::Arc;

use chrono::Utc;
use tabled::{settings::Style, Table, Tabled};
use venues::binance::coinm::{AccountTradeListRequest, Errors, PrivateRestClient};

#[derive(Tabled)]
pub struct TradeRow {
    #[tabled(rename = "Trade ID")]
    pub id: u64,

    #[tabled(rename = "Side")]
    pub side: String,

    #[tabled(rename = "Price")]
    pub price: String,

    #[tabled(rename = "Quantity")]
    pub quantity: String,

    #[tabled(rename = "Time")]
    pub time: String,
}

pub async fn handle_trades_command(
    client: Arc<PrivateRestClient>,
    symbol: String,
    limit: u32,
) -> Result<(), Errors> {
    let mut trades = Vec::new();
    let mut from_id = 0_u64;
    let mut page_count = 0;
    const MAX_PAGES: u32 = 1000;

    loop {
        if page_count >= MAX_PAGES {
            break;
        }

        // Create request with current pagination state
        let now = Utc::now().timestamp_millis() as u64;
        let trade_req = AccountTradeListRequest {
            symbol: Some(symbol.clone()),
            from_id: Some(from_id),
            limit: Some(limit),
            timestamp: now,
            ..Default::default()
        };

        // Fetch page of trades
        let resp = client.get_account_trades(trade_req).await?;
        if resp.data.is_empty() {
            break;
        }

        // Update pagination state
        if let Some(last_trade) = resp.data.iter().max_by_key(|t| t.id) {
            from_id = last_trade.id + 1;
        }

        trades.extend(resp.data);
        page_count += 1;
    }

    let mut rows: Vec<TradeRow> = trades
        .iter()
        .map(|trade| TradeRow {
            id: trade.id,
            side: format!("{:?}", trade.side),
            price: trade.price.to_string(),
            quantity: trade.quantity.to_string(),
            time: trade.time.to_string(),
        })
        .collect();

    rows.sort_by(|a, b| a.id.cmp(&b.id));

    println!("Trades for {} ({} total):", symbol, rows.len());
    let mut table = Table::new(rows);
    table.with(Style::rounded());
    println!("{table}");

    Ok(())
}

// Example: Place a trade on Binance COIN-M using the coinm Rust module
// Loads API credentials from .env
use std::{env, sync::Arc};

use anyhow::Result;
use clap::{Parser, Subcommand};
use dotenv::dotenv;
use rest::secrets::SecretValue;
use secrecy::SecretString;
use venues::binance::coinm::{ApiError, Errors, PrivateRestClient, PublicRestClient, RateLimiter};

mod commands;
use commands::{
    handle_account_command, handle_all_orders_command, handle_batch_order_command,
    handle_order_command, handle_position_risk_command, handle_trades_command,
};

#[derive(Parser)]
#[command(author, version, about, long_about = None)]
struct Cli {
    /// Use testnet instead of mainnet
    #[arg(long)]
    prod: bool,

    #[command(subcommand)]
    command: Commands,
}

#[derive(Subcommand)]
enum Commands {
    /// Get account information
    Account,

    /// Get recent trades for a symbol
    Trades {
        /// Trading pair symbol (e.g., BTCUSD)
        #[arg(required = true)]
        symbol: String,
        /// Maximum number of trades to fetch
        #[arg(short, long, default_value = "100")]
        limit: u32,
    },

    /// Place a batch order
    BatchOrder {
        /// Trading pair symbol (e.g., BTCUSD)
        #[arg(required = true)]
        symbol: String,

        /// Order side (BUY or SELL)
        #[arg(required = true)]
        side: String,

        /// Order type (LIMIT or MARKET)
        #[arg(required = true)]
        order_type: String,

        /// Order quantity
        #[arg(required = true)]
        quantity: f64,

        /// Order price (required for LIMIT orders)
        #[arg(short, long)]
        price: Option<f64>,
    },

    /// Get exchange information
    ExchangeInfo,

    /// Place a new order
    Order {
        /// Trading pair symbol (e.g., BTCUSD)
        #[arg(required = true)]
        symbol: String,
        /// Order side (BUY or SELL)
        #[arg(required = true)]
        side: String,
        /// Order type (LIMIT or MARKET)
        #[arg(required = true)]
        order_type: String,
        /// Order quantity
        #[arg(short, long)]
        quantity: Option<f64>,
        /// Order price (required for LIMIT orders)
        #[arg(short, long)]
        price: Option<f64>,
    },

    /// Get all orders for a symbol
    AllOrders {
        /// Trading pair symbol (e.g., BTCUSD)
        #[arg(required = true)]
        symbol: String,
        /// Maximum number of orders to fetch
        #[arg(short, long, default_value = "100")]
        limit: u32,
    },

    /// Get position risk information
    PositionRisk,
}

