//             eprintln!("ME invalid timestamp: {}", msg);
//         }
//         BinanceCoinMAPIError::UnmappedApiError { code, msg } => {
//             eprintln!("Unmapped API error (code: {}): {}", code, msg);
//         }
//     }
//     std::process::exit(1);
