fn create_client(prod: bool) -> Result<Arc<PrivateRestClient>> {
    dotenv().ok();
    let api_key = env::var("API_KEY").expect("API_KEY not set");
    let api_secret = env::var("API_SECRET").expect("API_SECRET not set");

    let client = if !prod {
        PrivateRestClient::new(
            Box::new(SecretValue::new(SecretString::from(api_key))),
            Box::new(SecretValue::new(SecretString::from(api_secret))),
            "https://testnet.binancefuture.com".to_string(),
            RateLimiter::new(),
            reqwest::Client::new(),
        )
    } else {
        PrivateRestClient::new(
            Box::new(SecretValue::new(SecretString::from(api_key))),
            Box::new(SecretValue::new(SecretString::from(api_secret))),
            "https://dapi.binance.com".to_string(),
            RateLimiter::new(),
            reqwest::Client::new(),
        )
    };

    Ok(Arc::new(client))
}

#[tokio::main]
async fn main() -> Result<()> {
    let cli = Cli::parse();
    let client = create_client(cli.prod)?;

    match cli.command {
        Commands::Account => {
            if let Err(e) = handle_account_command(client.clone()).await {
                if let Some(_api_err) = e.downcast_ref::<ApiError>() {
                    match _api_err {
                        // handle__api_error(_api_err);
                        ApiError::RateLimitExceeded { .. } => eprintln!("Rate limit exceeded"),
                        _ => eprintln!("API Error: {_api_err}"),
                    }
                }
                return Err(e);
            }
        }
        Commands::Trades { symbol, limit } => {
            if let Err(e) = handle_trades_command(client.clone(), symbol, limit).await {
                match &e {
                    Errors::ApiError(_api_err) => match _api_err {
                        ApiError::RateLimitExceeded { .. } => eprintln!("Rate limit exceeded"),
                        ApiError::BadSymbol { msg } => eprintln!("Bad symbol dsfsffdf: {msg}"),
                        _ => eprintln!("API Error: {_api_err}"),
                    },
                    _ => eprintln!("Unexpected error: {e}"),
                }
                return Err(e.into());
            }
        }
        Commands::BatchOrder {
            symbol,
            side,
            order_type,
            quantity,
            price,
        } => {
            if let Err(e) = handle_batch_order_command(
                client.clone(),
                symbol,
                side,
                order_type,
                quantity,
                price,
            )
            .await
            {
                if let Some(_api_err) = e.downcast_ref::<ApiError>() {
                    //handle__api_error(_api_err);
                }
                return Err(e);
            }
        }
        Commands::Order {
            symbol,
            side,
            order_type,
            quantity,
            price,
        } => {
            if let Err(e) =
                handle_order_command(client.clone(), symbol, side, order_type, quantity, price)
                    .await
            {
                if let Some(_api_err) = e.downcast_ref::<ApiError>() {
                    //handle__api_error(_api_err);
                }
                return Err(e);
            }
        }
        Commands::AllOrders { symbol, limit } => {
            if let Err(e) = handle_all_orders_command(client.clone(), symbol, limit).await {
                eprintln!("Error fetching all orders: {e}");
                return Err(e);
            }
        }
        Commands::PositionRisk => {
            if let Err(e) = handle_position_risk_command(client.clone()).await {
                eprintln!("Error fetching position risk: {e}");
                return Err(e);
            }
        }
        Commands::ExchangeInfo => {
            let public_client = PublicRestClient::new(
                "https://dapi.binance.com".to_string(),
                reqwest::Client::new(),
                RateLimiter::new(),
            );
            let public_client = Arc::new(public_client);
            if let Err(e) = commands::handle_exchange_info_command(public_client.clone()).await {
                if let Some(_api_err) = e.downcast_ref::<ApiError>() {
                    //handle__api_error(_api_err);
                }
                return Err(e);
            }
        }
    }

    Ok(())
}

