    let client = RestClient::new(
        Box::new(StaticSecret::new(api_key)),
        Box::new(StaticSecret::new(api_secret)),
        Box::new(StaticSecret::new(api_passphrase)),
        base_url,
        Client::new(),
        RateLimiter::new(),
    );

    println!("=== Coinbase Exchange Private REST API Example ===\n");

