            println!("  Best bid: {} @ {}", best_bid[1], best_bid[0]);
            // Bids should be in descending order
            if response.data.bids.len() > 1 {
                let next_bid = &response.data.bids[1];
                assert!(
                    best_bid[0] >= next_bid[0],
                    "Bids should be in descending price order"
                );
            }
        }
    }
}

